function updateClock(){var e=new Date,t=["January","February","March","April","May","June","July","August","September","October","November","December"];time=e.getHours()+":"+(e.getMinutes()<10?"0":""+e.getMinutes()),date=[t[e.getMonth()],e.getDate()+",",e.getFullYear()].join(" "),document.querySelector(".time").innerHTML=time,document.querySelector(".date").innerHTML=date,setTimeout(updateClock,1e3)}!function(){updateClock()}();